#include "pepsi_cola_factory.h"pepsi_cola_factory::pepsi_cola_factory( ) {	water_ = new pepsi_cola_water( );	bottle_ = new pepsi_cola_bottle( );}pepsi_cola_factory::~pepsi_cola_factory( ){	delete water_;	delete bottle_;}const abstract_water  * pepsi_cola_factory::create_water ( void ){ return water_;  }const abstract_bottle * pepsi_cola_factory::create_bottle( void ){ return bottle_; }