#include "caretaker.h"#include "originator.h"#include <cstring>#include <iostream>game_satae::game_satae() : mem_( NULL ) { }void game_satae::save_state( IOriginator *originator ){	mem_ = originator->get_memento();	std::cout<<"Save satate"<<std::endl;}void game_satae::load_state( IOriginator *originator ){	if(!mem_)	{		throw std::string("object did not save");	}	originator->set_memento(mem_);	std::cout<<"Load state"<<std::endl;}