#include "abstract_factory.h"#include "coca_cola_botte.h"#include "coca_cola_water.h"#include "coca_cola_factory.h"coca_cola_factory::coca_cola_factory( ){	water_ = new coca_cola_water( );	bottl_ = new coca_cola_bottle( );}coca_cola_factory::~coca_cola_factory( ){	delete water_;	delete bottl_;}coca_cola_water* coca_cola_factory::create_water ( ) { return water_;  }coca_cola_bottle* coca_cola_factory::create_bottle( ) { return bottl_;  }